mapper.select:
SELECT a.*, b.NAME AS categoryName, group_concat( t.NAME SEPARATOR ',' ) AS tagNames FROM b_article a JOIN b_category b ON a.category_id = b.id JOIN b_article_tag atg ON a.id = atg.article_id JOIN b_tags t ON atg.tag_id = t.id
a.user_id =#{param.userId}
a.title like concat('%',#{param.title},'%')
and a.category_id=#{param.categoryId}
and a.is_publish=#{param.isPublish}
and atg.tag_id=#{param.tagId}
GROUP BY a.id order by a.is_stick desc ,a.create_time desc

mapper.update:
update b_article
set is_publish=#{article.isPublish}
where id = #{article.id}

update b_article
set is_stick = #{article.isStick}
where id = #{article.id}
